css:
/* Apply global font settings */
body, .print-format {
    font-family: "Arial", sans-serif !important;
    font-size: 12px !important;
    color: #000;
    padding: 0 !important;
    margin: 0 !important;
}

/* Adjust table and text sizing */
.print-format table,
.print-format th,
.print-format td {
    font-size: 13px !important;
    line-height: 1.4 !important;
}

/* Reduce page margins for print */
@media print {
    @page {
        margin: 5mm !important;
    }

    body {
        margin: 0 !important;
        padding: 0 !important;
    }
}

/* Optional: widen content area */
.print-format {
    width: 100% !important;
}

/* Apply to all tables inside the print format */
.print-format table,
.print-format th,
.print-format td,
.print-format thead,
.print-format tbody,
.print-format tfoot {
    font-size: 13px !important;
    line-height: 0.5 !important;
    color: #000 !important;
}

/* Also target nested elements */
.print-format table * {
    font-size: 12px !important;
    color: #000 !important;

}

.print-format th {
    font-weight: bold !important;
    font-size: 12px !important;
    line-height: 1.4 !important;
}

.print-format .bank-details {
    font-size: 14px;
    line-height: 1.4;
}


.print-format .additional-details {
    font-size: 12px;
    line-height: 0.5;
}
